mapper.update:
update tbl_tran

set
stage=#{stage},
editBy=#{editBy},
editTime=#{editTime}

where id=#{id}

mapper.select:
select
t.id, u.name as owner, t.money, t.name, t.expectedDate, t.stage, t.type
from tbl_tran t join tbl_user u on t.owner = u.id where customerId=#{customerId}

mapper.update:
update tbl_tran
set
owner=#{owner}, money=#{money}, name=#{name}, expectedDate=#{expectedDate}, customerId=#{customerId}, stage=#{stage}, type=#{type}, source=#{source}, activityId=#{activityId}, contactsId=#{contactsId}, description=#{description}, contactSummary=#{contactSummary}, nextContactTime=#{nextContactTime}, editBy=#{editBy}, editTime=#{editTime}
where id=#{id}

mapper.delete:
delete from tbl_tran
where id in

    #{id}
